import groovy.time.TimeCategory 
import com.eviware.soapui.SoapUI

   def log = SoapUI.log
   def index = context.getCurrentStepIndex()+1
   def testStep = testRunner.testCase.getTestStepAt(index)
   def request = testStep.getTestRequest()

   def languageId = testRunner.testCase.testSuite.getPropertyValue( "languageId" )
   def categoryId = testRunner.testCase.testSuite.getPropertyValue( "categoryId" )
   def eventTypeId = testRunner.testCase.testSuite.getPropertyValue( "eventTypeId" )
   def companyId = testRunner.testCase.testSuite.getPropertyValue( "companyId Sidney" )

   testStep.setPropertyValue( "categoryId", categoryId )
   testStep.setPropertyValue( "languageId", languageId )
   testStep.setPropertyValue( "eventTypeId", eventTypeId )
   testStep.setPropertyValue( "companyId", companyId )


   def builder = new groovy.json.JsonBuilder()
   def map = [:]

   def currentDate = new Date()

   	use( TimeCategory ) {
    		startDate = currentDate + 120.minutes
	}

	use( TimeCategory ) {
    		endDate = currentDate + 180.minutes
	}

   map.put('eventHeader', 'Should not show in test')
   map.put('eventTextBody', 'The startdate is later than nowdate')
   map.put('startDate', startDate)
   map.put('endDate', endDate)
   map.put('maxRedeem', 2)

   builder(map)
   request.setRequestContent(builder.toString())

