def languageId = messageExchange.modelItem.testStep.testCase.getPropertyValue("languageId")
def languageId2 = messageExchange.modelItem.testStep.testCase.getPropertyValue("languageId2")
def eventTypeId = messageExchange.modelItem.testStep.testCase.getPropertyValue("eventTypeId")

def eventTypeTextId1 = messageExchange.modelItem.testStep.testCase.getPropertyValue("eventTypeTextId1")
def eventTypeTextId2 = messageExchange.modelItem.testStep.testCase.getPropertyValue("eventTypeTextId2")


//log.info 'eventTypeTextId1 ' + eventTypeTextId1
//log.info 'eventTypeTextId2 ' +eventTypeTextId2

assert json.code == 200

assert json.data.id == eventTypeId
assert json.data.defaultLanguage == languageId
assert json.data.description == 'Updated description'
assert json.data.languages.size() == 2

assert json.data.languages[0].id == eventTypeTextId1
assert json.data.languages[0].language == languageId
assert json.data.languages[0].value == 'UpdatedCategoryName'

assert json.data.languages[1].id == eventTypeTextId2
assert json.data.languages[1].language == languageId2
assert json.data.languages[1].value == 'SecondLanguage'

